
#define kLTParserSpecsData @"{\"@layout\":[{\"@isa\":\"UILabel\",\"@id\":\"l1\",\"backgroundColor\":\"@color-hex(00abb0)\",\"text\":\"the value is #{@bind(object.foo)}.\",\"textColor\":\"@color(white)\",\"translatesAutoresizingMaskIntoConstraints\":false,\"textAlignment\":1,\"cornerRadius\":8, \"hidden\":\"@context-value(hide)\",\"@subviews\":[{\"@isa\":\"UIView\",\"@id\":\"image1\",\"frame\":[\"@metric(#{l1.frame.size.height}+10+(2*#{l2.frame.size.width}))\",0,100,100]}]},{\"@isa\":\"UILabel\",\"@id\":\"l2\",\"backgroundColor\":\"@color-hex(00CCF0)\",\"text\":\"Hello #{object.foo} and #{@bind(object.bar)}\",\"textColor\":\"@color(white)\",\"translatesAutoresizingMaskIntoConstraints\":false,\"textAlignment\":1,\"cornerRadius\":8}],\"@constraints\":[{\"format\":\"H:|-margin-[l1(140)]-margin-[l2(80)]\",\"options\":[\"baseline\"],\"metrics\":{\"margin\":30}},{\"format\":\"V:|-[l1(50)]\"},{\"format\":\"V:|-[l2(50)]\"}]}"

#define kLTViewSpecsData @"{\"@layout\":[{\"@isa\":\"UIView\",\"@id\":\"v1\",\"backgroundColor\":\"@color-hex(00aff0)\",\"frame\":[0,0,10,44],\"@subviews\":[{\"@isa\":\"UIView\",\"@id\":\"v2\",\"backgroundColor\":\"@color-hex(cacaca)\",\"frame\":[0,0,\"@metric(#{v1.frameWidth}/2)\",44]}]},{\"@isa\":\"UILabel\",\"@id\":\"l1\",\"backgroundColor\":\"@color(clear)\",\"text\":\"hej: #{@bind(object.status)}\",\"textColor\"\"@color(black)\",\"frame\":[\"@metric(#{v1.frameWidth} + 10)\",0,300,40]}]}"